mapper.update:
update chat_member set notreadmessage = 0
where fk_cr_idx = #{cridx}
and fk_member_idx = #{idx}

update chat_member set cm_status = 0
where fk_cr_idx = #{cridx}
and fk_member_idx = #{idx}

mapper.select:
select m.idx as idx, m.name as name, m.img as img, m.userid as userid from tbl_teamwon tw join tbl_member m on tw.fk_member_idx = m.idx where tw.fk_team_idx = #{tidx} and tw.status != 0 and m.status = 1
and m.idx NOT IN
${midxArr[i]}

select idx, name, img, userid from tbl_member where status = 1
and idx NOT IN
${midxArr[i]}

select idx, name, img, userid from tbl_member where status = 1 and ${what} like '%' || #{subject} ||'%'
and idx NOT IN
${midxArr[i]}
