mapper.select:
select count(*)
from tbl_worktime
where fk_member_idx = #{idx}

select count(*)
from tbl_worktime
where fk_member_idx = #{idx}
and substr(wt_date,4,2) = #{month}

select count(*)
from tbl_worktime wt join tbl_teamwon tw
on wt.fk_member_idx = tw.fk_member_idx
where tw.idx = #{twidx}
and (((tw.disdate is null) and (trunc(wt.wt_date) >= trunc(tw.regdate))) or ((tw.disdate is not null) and (trunc(wt.wt_date) >= trunc(tw.regdate)) and (trunc(wt.wt_date) <= trunc(tw.disdate))))
and substr(wt.wt_date,4,2) = #{month}
