mapper.select:
select idx, fk_team_idx, userid, name, regdate, disdate, img, status
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.userid, V.name, V.regdate, V.disdate, V.img, V.status
    from (
        select idx, fk_team_idx, userid, name, regdate, disdate, img, status
		from view_memberlist
		where fk_team_idx = #{fk_team_idx}
		order by idx desc
    ) V
) T
where T.RNO >= #{startRno} and T.RNO <= #{endRno}


select idx, fk_team_idx, userid, name, regdate, disdate, img, status
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.userid, V.name, V.regdate, V.disdate, V.img, V.status
    from (
        select idx, fk_team_idx, userid, name, regdate, disdate, img, status
		from view_memberlist
		where fk_team_idx = #{fk_team_idx}
		and ${colname} like '%'|| #{search} ||'%'
		order by idx desc
    ) V
) T
where T.RNO >= #{startRno} and T.RNO <= #{endRno}

select count(*)
from view_memberlist
where fk_team_idx = #{fk_team_idx}

select idx
from tbl_teamwon
where fk_team_idx = #{fk_team_idx}
and fk_member_idx = #{fk_member_idx}

select status
from tbl_teamwon
where fk_team_idx = #{fk_team_idx}
and fk_member_idx = #{fk_member_idx}

select idx, fk_team_idx, userid, name, birth1, birth2, birth3, hp1, hp2, hp3,
                   email, post1, post2, addr1, addr2, profile
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.userid, V.name, V.birth1, V.birth2, V.birth3, V.hp1, V.hp2, V.hp3,
                   V.email, V.post1, V.post2, V.addr1, V.addr2, V.profile
    from (
        select idx, fk_team_idx, userid, name, birth1, birth2, birth3, hp1, hp2, hp3,
                   email, post1, post2, addr1, addr2, profile
        from view_addresslist
        where fk_team_idx = #{fk_team_idx}
        order by idx desc
    ) V
) T
where T.RNO >= #{startRno} and T.RNO <= #{endRno}

select idx, fk_team_idx, userid, name, birth1, birth2, birth3, hp1, hp2, hp3,
                   email, post1, post2, addr1, addr2, profile
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.userid, V.name, V.birth1, V.birth2, V.birth3, V.hp1, V.hp2, V.hp3,
                   V.email, V.post1, V.post2, V.addr1, V.addr2, V.profile
    from (
        select idx, fk_team_idx, userid, name, birth1, birth2, birth3, hp1, hp2, hp3,
                   email, post1, post2, addr1, addr2, profile
        from view_addresslist
        where fk_team_idx = #{fk_team_idx}
        and ${colname} like '%'|| #{search} ||'%'
        order by idx desc
    ) V
) T
where T.RNO >= #{startRno} and T.RNO <= #{endRno}
