mapper.update:
update tbl_teamwon set disdate = sysdate
where fk_team_idx = #{fk_team_idx}
and fk_member_idx = #{fk_member_idx}

mapper.select:
select idx, fk_team_idx, fk_member_idx, userid, name, status, regdate, disdate
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.fk_member_idx, V.userid, V.name, V.status, V.regdate, V.disdate
    from (
        select A.idx, A.fk_team_idx, A.fk_member_idx, B.userid, B.name, A.status, A.regdate, A.disdate
        from tbl_teamwon A join tbl_member B
        on A.fk_member_idx = B.idx
        where A.status = 1
        and A.fk_team_idx = #{fk_team_idx}
        and disdate is not null
        order by A.idx desc
    ) V
) T
where T.RNO >= 1 and T.RNO <= 5

select idx, fk_team_idx, fk_member_idx, userid, name, status, regdate, disdate
from (
    select rownum as RNO, V.idx, V.fk_team_idx, V.fk_member_idx, V.userid, V.name, V.status, V.regdate, V.disdate
    from (
        select A.idx, A.fk_team_idx, A.fk_member_idx, B.userid, B.name, A.status, A.regdate, A.disdate
        from tbl_teamwon A join tbl_member B
        on A.fk_member_idx = B.idx
        where A.status = 1
        and A.fk_team_idx = #{fk_team_idx}
        and ${colname1} like '%'|| #{search1} ||'%'
        and disdate is not null
        order by A.idx desc
    ) V
) T
where T.RNO >= 1 and T.RNO <= 5

